        width: '225px',
        height: '500px',
        margin: '0 10px 40px 10px',
        padding: '5px',
        backgroundColor: 'darkslategray',
        border: '1px solid',
        borderColor: 'rgb(255, 255, 255)',
        borderRadius: '5px',
    },

    cardImage: {
        width: '90%',
        height: '280px',
        marginBottom: '20px',
        marginTop: '20px'
    },

    cardTitle: {
        height: '30px',
        fontSize: '20px',
